full_step_json:
{
  "name": "Output and save logcat to file",
  "if": "${{ success() || steps.test.conclusion == 'failure'}}",
  "run": "mkdir -p mobile/build\nadb logcat -d > mobile/build/logcat.log\nadb logcat -v color &\n"
}

{
  "name": "Screenshot",
  "if": "${{ success() || steps.test.conclusion == 'failure'}}",
  "env": {
    "SUFFIX": "${{ matrix.android_avd }}-eAPI-${{ matrix.android_emu_version }}-${{ matrix.os }}"
  },
  "run": "adb shell monkey -p net.activitywatch.android.debug 1\nsleep 10\nscreencapture screenshots/pscreenshot-$SUFFIX.jpg\n$ANDROID_HOME/platform-tools/adb exec-out screencap -p > screenshots/pemulator-$SUFFIX.png\nls -alh screenshots/\n"
}

{
  "name": "Take AVD screenshots (Pixel 2 XL)",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 28,
    "profile": "pixel_2_xl",
    "script": "# Demo mode\nadb shell settings put global sysui_demo_allowed 1\nadb shell am broadcast -a com.android.systemui.demo -e command enter\nadb shell am broadcast -a com.android.systemui.demo -e command clock -e hhmm 0941\n# Run\nflutter drive --target=test_driver/screenshots_android.dart --flavor foss\n"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run \\\n  --type instrumentation \\\n  --app app/build/outputs/apk/debug/app-debug.apk \\\n  --test app/build/outputs/apk/androidTest/debug/app-debug-androidTest.apk \\\n  --device model=Pixel2,version=28,locale=en_US,orientation=portrait \\\n  --timeout 20m \\\n  --environment-variables coverage=true,coverageFile=/sdcard/coverage.ec --directories-to-pull=/sdcard/\n"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run --no-auto-google-login --type instrumentation --app app/build/outputs/apk/mock/debug/app-mock-debug.apk --test app/build/outputs/apk/androidTest/mock/debug/app-mock-debug-androidTest.apk --device model=Nexus6P,version=27,locale=en_US,orientation=portrait --environment-variables coverage=true,coverageFile=/sdcard/coverage.ec --directories-to-pull=/sdcard --timeout 20m"
  }
}

{
  "name": "Run connected android tests",
  "run": "export ANDROID_NDK_HOME=$(pwd)/android-ndk-r21e; export SWIFT_ANDROID_ARCH=x86_64; export SWIFT_ANDROID_HOME=$(pwd)/swift-android-current; export PATH=$ANDROID_NDK_HOME:$PATH; export PATH=$SWIFT_ANDROID_HOME/bin:$SWIFT_ANDROID_HOME/build-tools/current:$PATH; adb wait-for-device; cd core; sourcery; swift-test"
}

{
  "name": "AVD cache",
  "uses": "actions/cache@v3",
  "id": "avd-cache",
  "with": {
    "path": "~/.android/avd/*\n~/.android/adb*\n",
    "key": "avd-${{ matrix.api-level }}-${{ matrix.arch }}-${{matrix.target}}-v1-${{ github.event.inputs.clearCaches }}",
    "restore-keys": "avd-${{ matrix.api-level }}-${{ matrix.arch }}-${{matrix.target}}-v1\n"
  }
}

{
  "name": "Emulator Snapshot After Firstboot Warmup",
  "if": "${{ github.event.inputs.clearCaches != '' || (steps.avd-cache.outputs.cache-hit != 'true' && github.ref == 'refs/heads/main') }}",
  "env": {
    "FIRST_BOOT_DELAY": "${{ matrix.first-boot-delay }}"
  },
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "force-avd-creation": false,
    "target": "${{ matrix.target }}",
    "arch": "${{ matrix.arch }}",
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "sdcard-path-or-size": "100M",
    "disable-animations": true,
    "script": "touch adb-log.txt\n$ANDROID_HOME/platform-tools/adb logcat '*:D' >> adb-log.txt &\n$ANDROID_HOME/platform-tools/adb shell su root \"setprop ctl.restart zygote\"\nsleep 10\nsleep $FIRST_BOOT_DELAY\nadb logcat --clear\necho \"First boot warmup completed.\"\n"
  }
}

{
  "name": "Android Emulator test",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ env.api-level }}",
    "target": "aosp_atd",
    "channel": "canary",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "zsh .github/workflows/runEmulatorTests.sh"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run --type instrumentation --app app/build/outputs/apk/debug/app-debug.apk --test app/build/outputs/apk/androidTest/debug/app-debug-androidTest.apk --device model=Nexus5X,version=26,locale=en_US,orientation=portrait --environment-variables coverage=true,coverageFile=/sdcard/tmp/code-coverage/connected/coverage.ec --directories-to-pull=/sdcard/tmp --timeout 20m"
  }
}

{
  "name": "Create AVD and generate snapshot for caching",
  "if": "steps.avd-cache.outputs.cache-hit != 'true'",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 28,
    "target": "google_apis",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": false,
    "script": "echo \"Generated AVD snapshot for caching.\""
  }
}

{
  "name": "Run tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 28,
    "target": "google_apis",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew connectedCheck"
  }
}

{
  "name": "Create AVD and generate snapshot for caching",
  "if": "steps.avd-cache.outputs.cache-hit != 'true'",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": false,
    "script": "echo \"Generated AVD snapshot for caching.\""
  }
}

{
  "name": "Run tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew app:connectedStandardDebugAndroidTest"
  }
}

{
  "name": "Executing unit and android tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 24,
    "arch": "x86_64",
    "disable-animations": true,
    "script": "./gradlew installDebug\nadb shell appops set ch.epfl.qedit android:mock_location allow\n./gradlew build connectedCheck jacocoTestReport\n"
  }
}

{
  "name": "Publish Test Report of Instrumentation Tests",
  "uses": "mikepenz/action-junit-report@v3",
  "if": "always()",
  "with": {
    "check_name": "Instrumentation Test Report",
    "report_paths": "app/build/outputs/androidTest-results/connected/TEST-*.xml"
  }
}

{
  "name": "\ud83e\udd16 UI tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 29,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew app:connectedAndroidTest"
  }
}

{
  "name": "\ud83e\udd16 UI tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 29,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew app:connectedAndroidTest"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run \\\n  --type instrumentation \\\n  --app app/build/outputs/apk/debug/app-debug.apk \\\n  --test app/build/outputs/apk/androidTest/debug/app-debug-androidTest.apk \\\n  --device model=Nexus5X,version=26,locale=en_US,orientation=portrait \\\n  --environment-variables coverage=true,coverageFile=\"/sdcard/coverage.ec\" \\\n  --directories-to-pull=/sdcard --timeout 30m\n",
    "no_output_timeout": "45m"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run --type instrumentation --app app/build/outputs/apk/debug/app-debug.apk --test app/build/outputs/apk/androidTest/debug/app-debug-androidTest.apk --device model=blueline,version=28,locale=en_US,orientation=portrait --use-orchestrator --environment-variables clearPackageData=true,coverage=true,coverageFilePath=/sdcard/ --directories-to-pull=/sdcard --timeout 20m\n"
  }
}

{
  "name": "Generate baseline profile",
  "if": "${{ inputs.build_release_apks }}",
  "env": {
    "KEYSTORE_PASSWORD": "${{ secrets.keystore_password }}",
    "KEYSTORE_KEY_PASSWORD": "${{ secrets.keystore_key_password }}",
    "TWITTER_CLIENT_ID": "${{ secrets.twitter_client_id }}",
    "UNSPLASH_ACCESS_KEY": "${{ secrets.unsplash_access_key }}"
  },
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 31,
    "target": "default",
    "arch": "x86_64",
    "profile": "pixel",
    "avd-name": "emulator",
    "script": "./gradlew macrobenchmark:connectedBenchmarkAndroidTest  -Pandroid.testInstrumentationRunnerArguments.class=any.macrobenchmark.BaselineProfileGenerator  -Pandroid.testoptions.manageddevices.emulator.gpu=\"swiftshader_indirect\"  -PdisableSplits\n"
  }
}

{
  "name": "Copy baseline profile to app module",
  "if": "${{ inputs.build_release_apks }}",
  "run": "profile_path=\"./android/macrobenchmark/build/outputs/\\\nconnected_android_test_additional_output/benchmark/connected/emulator(AVD) - 12/\\\nBaselineProfileGenerator_generate-baseline-prof.txt\"\noutput_path=\"./android/app/src/main/baseline-prof.txt\"\ncp \"$profile_path\" \"$output_path\"\n",
  "shell": "bash"
}

{
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "arch": "x86",
    "disable-animations": true,
    "script": "./gradlew uicomponent-compose:main:connectedCheck uicomponent-compose:feed:connectedCheck uicomponent-compose:other:connectedCheck uicomponent-compose:core:connectedCheck"
  }
}

{
  "name": "Run ui test on casa-ui module via gmd",
  "if": "contains(github.event.pull_request.labels.*.name, 'casa-ui')",
  "run": "./gradlew :casa-ui:pixel4api30aospatdDebugAndroidTest -Pandroid.testoptions.manageddevices.emulator.gpu=\"swiftshader_indirect\""
}

{
  "name": "Create AVD and generate snapshot for caching",
  "if": "steps.avd-cache.outputs.cache-hit != 'true'",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": false,
    "script": "echo \"Generated AVD snapshot for caching.\""
  }
}

{
  "name": "Run tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew :app:connectedBasicDebugAndroidTest -Pandroid.testInstrumentationRunnerArguments.package=nl.eduvpn.app.service $SKIP_ICS_OPENVPN_BUILD"
  }
}

{
  "name": "Run instrumentation tests with Firebase Test Lab",
  "run": "sudo gcloud firebase test android run \\\n  --app app/build/outputs/apk/debug/app-debug.apk \\\n  --test app/build/outputs/apk/androidTest/debug/app-debug-androidTest.apk \\\n  --device model=Pixel2,version=28,locale=en,orientation=portrait \\\n  --directories-to-pull /sdcard \\\n  --environment-variables coverage=true,coverageFile=\"/sdcard/coverage.ec\" \\\n  --results-bucket cloud-test-${{ secrets.GOOGLE_PROJECT_ID }}\n"
}

{
  "name": "Run Android tests",
  "if": "startsWith(matrix.os, 'macos')",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 31,
    "arch": "x86_64",
    "script": "./gradlew connectedCheck || { adb logcat -d; exit 1; }"
  }
}

{
  "name": "Android Emulator Runner",
  "uses": "ReactiveCircus/android-emulator-runner@v2.23.0",
  "with": {
    "api-level": 31,
    "target": "playstore",
    "arch": "x86_64",
    "script": "./gradlew connectedCheck --exclude-task json:test --exclude-task app:processDebugGoogleServices --exclude-task app:processReleaseGoogleServices"
  }
}

{
  "name": "Run unit/instrumentation tests and coverage generation",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": 30,
    "target": "google_apis",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -no-snapshot -noaudio -no-boot-anim",
    "disable-animations": true,
    "script": "./gradlew -Pandroid.testInstrumentationRunnerArguments.notAnnotation=androidx.test.filters.FlakyTest,androidx.test.filters.LargeTest jacocoTestReport --stacktrace"
  }
}

{
  "run": {
    "name": "Run instrumented test on Firebase Test Lab",
    "command": "gcloud firebase test android run --type instrumentation --app apk/build/outputs/apk/debug/apk-debug.apk --test apk/build/outputs/apk/androidTest/debug/apk-debug-androidTest.apk --device model=sailfish,version=21,locale=en_US,orientation=portrait --directories-to-pull=/sdcard/tmp --timeout 20m"
  }
}

{
  "name": "Run tests in emulator",
  "uses": "reactivecircus/android-emulator-runner@d94c3fbe4fe6a29e4a5ba47c12fb47677c73656b",
  "with": {
    "api-level": 31,
    "target": "google_apis",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "cd mobile-app && if adb shell pm list packages | grep -q org.freecodecamp; then adb uninstall org.freecodecamp; fi && dart integration_test_runner.dart"
  }
}

{
  "name": "Run tests in emulator",
  "uses": "reactivecircus/android-emulator-runner@d94c3fbe4fe6a29e4a5ba47c12fb47677c73656b",
  "with": {
    "api-level": 31,
    "target": "google_apis",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "cd mobile-app && if adb shell pm list packages | grep -q org.freecodecamp; then adb uninstall org.freecodecamp; fi && dart integration_test_runner.dart"
  }
}

{
  "name": "Upload macOS release",
  "uses": "softprops/action-gh-release@v1",
  "if": "startsWith(github.ref, 'refs/tags/') && runner.os == 'macOS' && matrix.extra == 'test'",
  "with": {
    "files": "ppsspp/*.zip",
    "body": "PPSSPP is a cross-platform PSP emulator. Visit PPSSPP [official website](https://ppsspp.org) for a [full changelog](https://ppsspp.org/index.html#news) as well as the [downloads section](https://ppsspp.org/downloads.html) for other platforms.\n"
  }
}

{
  "name": "Install Android SDK and Emulator",
  "run": "SDK_MAN=$HOME/Library/Android/sdk/tools/bin/sdkmanager; BTOOLS=$($SDK_MAN --list | grep \"build-tools;$TARGET_SDK\"); BTOOL=$(echo $BTOOLS | tail -n1 | cut -d'|' -f1 | tr -d ' '); PLATFORMS=$($SDK_MAN --list | grep \"platforms;android-$TARGET_SDK\"); PLATFORM=$(echo $PLATFORMS | tail -n1 | cut -d'|' -f1 | tr -d ' ');\n$SDK_MAN --install \"$BTOOL\" platform-tools \"$PLATFORM\" emulator \"system-images;android-$TARGET_SDK;default;x86_64\" > /dev/null\necho no | $HOME/Library/Android/sdk/tools/bin/avdmanager create avd --force -n test --abi 'default/x86_64' --package \"system-images;android-$TARGET_SDK;default;x86_64\""
}

{
  "name": "Run Android Bridge tests",
  "working-directory": "android",
  "run": "$HOME/Library/Android/sdk/emulator/emulator -avd test -no-boot-anim -no-window -no-snapshot-save -gpu swiftshader_indirect -noaudio & EMULATOR_PID=$!;\n$HOME/Library/Android/sdk/platform-tools/adb wait-for-device shell 'while [[ -z $(getprop sys.boot_completed) ]]; do sleep 2; done;';\n./gradlew bridge:test bridge:connectedAndroidTest; kill $EMULATOR_PID"
}

{
  "name": "create AVD and generate snapshot for caching",
  "if": "steps.avd-cache.outputs.cache-hit != 'true'",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": false,
    "script": "echo \"Generated AVD snapshot for caching.\""
  }
}

{
  "name": "run tests",
  "uses": "reactivecircus/android-emulator-runner@v2",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "force-avd-creation": false,
    "emulator-options": "-no-snapshot-save -no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none",
    "disable-animations": true,
    "script": "./gradlew connectedCheck"
  }
}

{
  "name": "Run Debug Instrumentation Tests on Android API ${{ matrix.api-level }} (Ran up to 2 times)",
  "uses": "reactivecircus/android-emulator-runner@v2.28.0",
  "with": {
    "api-level": "${{ env.ANDROID_API_LEVEL }}",
    "arch": "x86_64",
    "target": "google_apis",
    "force-avd-creation": false,
    "emulator-options": "-no-window -gpu swiftshader_indirect -noaudio -no-boot-anim -camera-back none -no-snapshot-save",
    "disable-animations": true,
    "script": "./gradlew connectedDebugAndroidTest -Pci -s || ./gradlew connectedDebugAndroidTest -Pci -s"
  }
}

{
  "run": {
    "name": "Install GCloud",
    "command": "echo \"deb http://packages.cloud.google.com/apt cloud-sdk-$(lsb_release -c -s) main\" | sudo tee -a /etc/apt/sources.list.d/google-cloud-sdk.list\ncurl https://packages.cloud.google.com/apt/doc/apt-key.gpg | sudo apt-key add -\nsudo apt-get update\nsudo apt-get install -y google-cloud-sdk\nsudo apt-get install --only-upgrade -y kubectl google-cloud-sdk google-cloud-sdk-datastore-emulator google-cloud-sdk-pubsub-emulator google-cloud-sdk-app-engine-go google-cloud-sdk-app-engine-java google-cloud-sdk-app-engine-python google-cloud-sdk-cbt google-cloud-sdk-bigtable-emulator google-cloud-sdk-datalab\n"
  }
}

{
  "uses": "actions/cache@v2",
  "with": {
    "path": "~/.gradle/caches\n~/.gradle/wrapper\n~/.android/avd/*\n~/.android/adb*\n",
    "key": "${{ runner.os }}-avd-${{ matrix.target }}-${{ matrix.apiLevel }}",
    "restore-keys": "${{ runner.os }}-avd-${{ matrix.target }}-${{ matrix.apiLevel }}-\n${{ runner.os }}-avd-${{ matrix.target }}-\n${{ runner.os }}-avd-\n"
  }
}

{
  "run": {
    "name": "Configure screen size and dpi for Screenshot test",
    "command": "./script/emulator/configure_dpi.sh"
  }
}

{
  "name": "create instrumentation coverage",
  "uses": "ReactiveCircus/android-emulator-runner@v2",
  "env": {
    "GRADLE_OPTS": "-Dorg.gradle.internal.http.connectionTimeout=60000 -Dorg.gradle.internal.http.socketTimeout=60000 -Dorg.gradle.internal.network.retry.max.attempts=6 -Dorg.gradle.internal.network.retry.initial.backOff=2000"
  },
  "if": "${{ matrix.api-level != 33 }}",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "target": "default",
    "arch": "x86_64",
    "profile": "pixel_2",
    "ram-size": "4096M",
    "disk-size": "14G",
    "sdcard-path-or-size": "1000M",
    "disable-animations": false,
    "script": "bash contrib/instrumentation.sh"
  }
}

{
  "name": "create instrumentation coverage on google_apis for android 33",
  "uses": "ReactiveCircus/android-emulator-runner@v2",
  "env": {
    "GRADLE_OPTS": "-Dorg.gradle.internal.http.connectionTimeout=60000 -Dorg.gradle.internal.http.socketTimeout=60000 -Dorg.gradle.internal.network.retry.max.attempts=6 -Dorg.gradle.internal.network.retry.initial.backOff=2000"
  },
  "if": "${{ matrix.api-level == 33 }}",
  "with": {
    "api-level": "${{ matrix.api-level }}",
    "target": "google_apis",
    "arch": "x86_64",
    "profile": "pixel_2",
    "heap-size": "512M",
    "ram-size": "4096M",
    "disk-size": "14G",
    "sdcard-path-or-size": "4096M",
    "disable-animations": false,
    "script": "bash contrib/instrumentation.sh"
  }
}

{
  "name": "Run instrumented tests with GMD",
  "run": "./gradlew cleanManagedDevices --unused-only && ./gradlew ${{ matrix.device-config }}debugAndroidTest -Dorg.gradle.workers.max=1 -Pandroid.testoptions.manageddevices.emulator.gpu=\"swiftshader_indirect\" -Pandroid.experimental.testOptions.managedDevices.emulator.showKernelLogging=true"
}

{
  "name": "Create AVD and generate snapshot for caching",
  "if": "steps.avd-cache.outputs.cache-hit != 'true'",
  "uses": "reactivecircus/android-emulator-runner@v2.27.0",
  "with": {
    "api-level": "${{ matrix.android }}",
    "arch": "x86_64",
    "force-avd-creation": false,
    "emulator-options": "-no-boot-anim -no-audio -no-snapshot -no-window -camera-back none -camera-front none -gpu swiftshader_indirect",
    "disable-animations": false,
    "script": "echo \"Generated AVD snapshot for caching.\""
  }
}

{
  "name": "Run UI Tests",
  "uses": "reactivecircus/android-emulator-runner@v2.27.0",
  "with": {
    "api-level": "${{ matrix.android }}",
